    private void getTanggal() {
        Date skrg = new Date();
        SimpleDateFormat tgl = new SimpleDateFormat("EEEE, dd-MMMM-yyyy");
        SimpleDateFormat jam = new SimpleDateFormat(" HH : mm ");
        jLabel3.setText(jam.format(skrg));
        jLabel4.setText(tgl.format(skrg));
    }
